<script>
import axios from "axios";

export default {
  data() {
    return {
      dialog: false,
      search: "",
      page: 1,
      itemsPerPage: "10",
      items: [],
      headers: [
        { key: "id", title: "ID" },
        { key: "serviceName", title: "Название" },
        { key: "description", title: "Описание" },
        { key: "duration", title: "Длительность" },
        { key: "cost", title: "Цена" },
        { key: "registrationDate", title: "Дата создания" },
        { key: "updateDate", title: "Дата обновления" },
        { key: "actions", title: " " },
      ],
    };
  },
  created() {
    this.fetchItems();
  },
  computed: {
    pageCount() {
      return Math.ceil(this.items.length / this.itemsPerPage);
    },
  },

  methods: {
    fetchItems() {
      axios
        .get("http://localhost:3000/masterServices")
        .then((response) => {
          this.items = response.data;
        })
        .catch((error) => {
          console.error("Error fetching items:", error);
        });
    },
  },
};
